{% if product.metafields.custom.drop == 'true' or product.metafields.custom.drop == true %}
<style>
  .frete-container {
    background-color: #ffffff;
    border-left: 4px solid #FF7A00;
    border-radius: 6px;
    padding: 14px 20px;
    margin: 20px auto;
    box-shadow: 0 2px 10px rgba(255, 122, 0, 0.08);
    text-align: center;
    max-width: 90%;
    transition: all 0.2s ease;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .frete-container:hover {
    box-shadow: 0 4px 15px rgba(255, 122, 0, 0.15);
  }

  .frete-icon {
    font-size: 22px;
    margin-right: 12px;
    color: #FF7A00;
  }

  .frete-mensagem {
    color: #333333;
    font-weight: 500;
    font-size: 16px;
    margin: 0;
    font-family: 'Poppins', -apple-system, BlinkMacSystemFont, sans-serif;
    letter-spacing: 0.2px;
  }

  .frete-destaque {
    font-weight: 600;
    color: #FF7A00;
    position: relative;
    padding-bottom: 2px;
  }

  .frete-destaque:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 2px;
    background-color: rgba(255, 122, 0, 0.3);
    border-radius: 10px;
  }

  @media (max-width: 768px) {
    .frete-container {
      padding: 12px 15px;
    }

    .frete-mensagem {
      font-size: 15px;
    }
  }
</style>

<div class="frete-container">
  <span class="frete-icon">üöö</span>
  <p id="frete-mensagem" class="frete-mensagem">Verificando disponibilidade de frete...</p>
</div>

<script>
  function obterCidade() {
    // Primeiro verifica se temos o CEP global salvo
    if (window.globalCep && typeof window.globalCep.getInfo === 'function') {
      return window.globalCep.getInfo()
        .then(info => {
          if (info && info.cidade) {
            return info.cidade;
          } else {
            // Se n√£o tiver info do CEP global, usa o IP
            return obterCidadePorIP();
          }
        })
        .catch(() => obterCidadePorIP());
    } else {
      return obterCidadePorIP();
    }
  }

  function obterCidadePorIP() {
    return fetch('https://ipapi.co/json/')
      .then(response => response.json())
      .then(data => {
        if (data.city) {
          return data.city;
        } else {
          return "sua cidade";
        }
      })
      .catch(() => "sua cidade");
  }

  document.addEventListener("DOMContentLoaded", function() {
    obterCidade().then(cidade => {
      const mensagem = `Frete Gr√°tis para <span class="frete-destaque">${cidade}</span>`;
      document.getElementById("frete-mensagem").innerHTML = mensagem;
    });
  });
</script>

{% else %}
  <div class="freight-calculator">
    <label for="cep-input" class="label">Calcular Frete</label>
    <div class="input-container">
      <input type="text" id="cep-input" class="input" placeholder="Digite seu CEP" />
      <button id="calculate-freight-btn" class="freight-button">Calcular</button>
    </div>

    <div id="freight-results" class="results">
      <div class="loading-spinner" style="display: none;"></div>
      <div class="results-header">
        <h3>Op√ß√µes de Entrega</h3>
        <p class="cep-info">CEP: <span id="cep-display"></span></p>
      </div>
      <div class="results-grid"></div>
      <p class="disclaimer">* Prazos calculados a partir da confirma√ß√£o do pagamento</p>
    </div>
  </div>

  <script>
    window.productData = {
      height: {{ product.metafields.custom.altura | default: 10 }},
      length: {{ product.metafields.custom.comprimento | default: 20 }},
