  </script>

  <style>
    .freight-calculator {
      max-width: 600px;
      margin: 20px auto;
      padding: 30px;
      background-color: #ffffff;
      border-radius: 12px;
      box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
    }

    .label {
      font-size: 1.4em;
      font-weight: bold;
      margin-bottom: 15px;
      color: #333;
      display: block;
      text-align: center;
    }

    .input-container {
      display: flex;
      gap: 15px;
      align-items: center;
