    }

    .results-grid {
      display: grid;
      gap: 15px;
    }

    .shipping-option {
      display: flex;
      align-items: center;
      padding: 18px;
      background: white;
      border-radius: 8px;
      border: 1px solid #e5e7eb;
    }

    .shipping-icon {
      width: 40px;
      height: 40px;
      margin-right: 15px;
      background: #e8f4ff;
