    }

    .loading-spinner {
      width: 40px;
      height: 40px;
      border: 4px solid #f3f3f3;
      border-top: 4px solid #0073e6;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin: 20px auto;
    }

    .shipping-error {
      padding: 20px;
      background: #ffe6e6;
      border: 1px solid #ff4d4d;
      border-radius: 8px;
      color: #cc0000;
      text-align: center;
    }

    .shipping-icon[data-carrier="Correios"] { background: #f5e6f7; }
    .shipping-icon[data-carrier="Azul"] { background: #e6f4ff; }
    .shipping-icon[data-carrier="Jadlog"] { background: #fff3e6; }

    .cep-info-highlight {
      font-weight: bold;
      color: #005bb5;
      text-align: center;
      margin-bottom: 15px;
    }

    .cep-highlight {
      color: #005bb5;
    }

    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }

    @media (max-width: 600px) {
      .freight-calculator {
        padding: 20px;
      }

      .input-container {
        flex-direction: column;
      }

      .input, .freight-button {
        width: 100%;
      }

      .shipping-info {
        flex-direction: column;
        gap: 5px;
      }
    }
  </style>

  <script>
    document.addEventListener('DOMContentLoaded', function () {
      console.log('DOM totalmente carregado.');
      const button = document.getElementById('calculate-freight-btn');
      const resultsContainer = document.querySelector('.results-grid');
      const cepDisplay = document.getElementById('cep-display');
      const loadingSpinner = document.querySelector('.loading-spinner');
      const quantityInput = document.getElementById('number');
      const cepInput = document.getElementById('cep-input');

      if (!button) {
        console.error('Bot√£o "Calcular Frete" n√£o encontrado.');
        return;
      }

      // Fun√ß√£o para formatar CEP com m√°scara
      function formatCep(cep) {
        cep = cep.replace(/\D/g, '');
        if (cep.length > 5) {
          cep = cep.substring(0, 5) + '-' + cep.substring(5, 8);
        }
        return cep;
      }

      // Aplicar m√°scara ao digitar
      if (cepInput) {
        cepInput.addEventListener('input', function(e) {
          let value = e.target.value.replace(/\D/g, '');
          if (value.length > 5) {
            value = value.substring(0, 5) + '-' + value.substring(5);
          }
          e.target.value = value;
        });
      }

      // Verificar se existe CEP global e preencher o campo
      function checkGlobalCep() {
        if (window.globalCep && typeof window.globalCep.getCep === 'function') {
          const savedCep = window.globalCep.getCep();
          if (savedCep && savedCep.length === 8) {
            cepInput.value = formatCep(savedCep);
            // Calcular frete automaticamente se tiver CEP
            calculateFreight(savedCep);
          }
        } else {
          // Fallback para localStorage se o globalCep n√£o estiver dispon√≠vel
          const savedCep = localStorage.getItem('shopify_customer_cep');
          if (savedCep) {
            cepInput.value = savedCep;
            calculateFreight(savedCep.replace(/\D/g, ''));
          }
        }
      }

      // Verificar CEP global quando a p√°gina carregar
      setTimeout(checkGlobalCep, 500); // Pequeno delay para garantir que globalCep esteja dispon√≠vel

      // Fun√ß√£o para calcular o frete
      async function calculateFreight(cepValue) {
        if (cepValue.length !== 8) {
          showError('CEP inv√°lido. Digite 8 n√∫meros.');
          return;
        }

        const quantity = parseInt(quantityInput?.value) || 1;

        toggleLoading(true);

        try {
          const requestData = {
            SellerCEP: parseFloat(window.productData.sellercep) || 35500006,
            RecipientCEP: cepValue,
            ShipmentInvoiceValue: parseFloat(window.productData.price),
            ShippingItemArray: [{
              Height: parseFloat(window.productData.height) || 10,
              Length: parseFloat(window.productData.length) || 20,
              Quantity: quantity,
              Weight: parseFloat(window.productData.weight) || 1,
              Width: parseFloat(window.productData.width) || 15
            }]
          };

          console.log('Dados enviados para a API:', requestData);

          const response = await fetch('https://serverfrnt.vercel.app/api/frenet-quote', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(requestData)
          });

          const data = await response.json();

          if (response.ok && data.ShippingSevicesArray?.length > 0) {
            const validServices = data.ShippingSevicesArray.filter(service => {
              const price = Number(service.ShippingPrice);
              return !isNaN(price) && price > 0;
            });

            if (validServices.length > 0) {
              displayResults(validServices, cepValue, data.City);

              // Salvar o CEP no sistema global
              if (window.globalCep && typeof window.globalCep.setCep === 'function') {
                window.globalCep.setCep(cepValue);
              } else {
                // Fallback para localStorage
                localStorage.setItem('shopify_customer_cep', formatCep(cepValue));
              }
            } else {
              showError('Nenhuma op√ß√£o de frete dispon√≠vel para o CEP informado.');
            }
          } else {
            showError('Nenhuma op√ß√£o de frete dispon√≠vel para o CEP informado.');
          }

        } catch (error) {
          console.error('Erro ao calcular o frete:', error);
          showError('Ocorreu um erro ao calcular o frete. Tente novamente mais tarde.');
        } finally {
          toggleLoading(false);
        }
      }

      // Evento de clique no bot√£o calcular
      button.addEventListener('click', function () {
        const cepValue = cepInput.value.replace(/\D/g, '');
        calculateFreight(cepValue);
      });

      // Permitir calcular com Enter
      cepInput.addEventListener('keypress', function(e) {
        if (e.key === 'Enter') {
          button.click();
        }
      });

      function displayResults(services, cep, city) {
        resultsContainer.innerHTML = '';

        // Formatar CEP para exibi√ß√£o
        const formattedCep = cep.replace(/(\d{5})(\d{3})/, '$1-$2');

        resultsContainer.innerHTML += `<div class="cep-info-highlight"><span class="cep-highlight">${formattedCep} - ${city}</span></div>`;

        services.forEach((service, index) => {
          const option = document.createElement('div');
          option.className = 'shipping-option';

          const shippingPrice = Number(service.ShippingPrice).toFixed(2);

          option.innerHTML = `
            <div class="shipping-icon" data-carrier="${service.Carrier}">${getCarrierIcon(service.Carrier)}</div>
            <div class="option-details">
              <div class="shipping-method">${service.ServiceDescription}</div>
              <div class="shipping-info">
                <span>üïí ${service.DeliveryTime} dias √∫teis</span>
                <span>üì¶ ${service.Carrier}</span>
              </div>
            </div>
            <div class="shipping-price">R$ ${shippingPrice}</div>
          `;

          resultsContainer.appendChild(option);
        });

        document.getElementById('freight-results').style.display = 'block';
      }

      function toggleLoading(show) {
        loadingSpinner.style.display = show ? 'block' : 'none';
      }

      function showError(message) {
        resultsContainer.innerHTML = `<div class="shipping-error">${message}</div>`;
        document.getElementById('freight-results').style.display = 'block';
      }

      function getCarrierIcon(carrier) {
        const icons = {
          'Correios': 'üöö',
          'Azul': '‚úàÔ∏è',
          'Jadlog': 'üìÆ',
          'DHL': 'üì¶',
          'Default': 'üöõ'
        };
        return icons[carrier] || icons.Default;
      }

      // Escutar por eventos de atualiza√ß√£o do CEP global
      document.addEventListener('cep_updated', function(e) {
        const newCep = e.detail.cep;
        if (newCep && cepInput) {
          cepInput.value = formatCep(newCep);
          calculateFreight(newCep);
        }
      });
    });
  </script>
{% endif %}
